package cn.carveknife.carveknifewanandroid.ui.homeimport android.util.Logimport cn.carveknife.carveknifewanandroid.data.entity.HomeEntityimport cn.carveknife.carveknifewanandroid.http.HttpManagerimport cn.carveknife.carveknifewanandroid.http.asyncSubscribeimport kotlin.math.sign/** * description : * created time: 2020/12/10 11:50 * created by: cuibenguang */class HomeDataRepository constructor(private val httpManager: HttpManager) : HomeRepository {    override suspend fun getArticleData(pageSize: Int): Article {        val articles = httpManager.wanApi.getArticles(pageSize)        if (articles.data is Article) {            return articles.data as Article        }        return Article()    }}